import type { A, L, S } from 'ts-toolbelt';
import { PartialDeep } from 'type-fest';
export declare type AnyObject = Record<string, any>;
export declare type UnknownObject = Record<string, unknown>;
export declare type ArrayElement<ARRAY_TYPE extends readonly unknown[]> = ARRAY_TYPE[number];
export declare type DeepPartial<T> = PartialDeep<T>;
export declare type Constructor<T = AnyObject, ARGS extends unknown[] = any[]> = new (...args: ARGS) => T;
export declare type Abstract<T> = Function & {
    prototype: T;
};
export declare type PickWhere<T, U> = Pick<T, {
    [K in keyof T]: T[K] extends U ? K : never;
}[keyof T]>;
export declare type OmitWhere<T, U> = {
    [K in keyof T as Required<T>[K] extends U ? never : K]: T[K];
};
export declare type FilterKey<K, I> = A.Equals<K, I> extends 1 ? never : K;
export declare type OmitIndex<T> = {
    [K in keyof T as FilterKey<K, IndexSignature<T>>]: T[K];
};
export declare type IndexSignature<T> = {
    [K in keyof T]: K extends infer P ? P : never;
}[keyof T];
export declare type PartialWhere<T, K extends keyof T> = Omit<T, K> & Partial<T>;
export declare type RequiredOnly<T> = {
    [K in keyof OmitIndex<T> as UnknownObject extends Pick<OmitIndex<T>, K> ? never : K]: OmitIndex<T>[K] extends UnknownObject ? RequiredOnly<OmitIndex<T>[K]> : OmitIndex<T>[K];
};
export declare type FirstKey<K extends string> = Shift<S.Split<K, '.'>>;
export declare type Shift<S extends L.List> = S extends readonly [infer P, ...unknown[]] ? P : never;
export declare type KeyOf<T, K> = K extends keyof T ? K : never;
export declare type RequiredOnlyWhere<T, K extends string> = L.Length<S.Split<K, '.'>> extends 1 ? PartialDeep<T> & {
    [KEY in keyof T as KEY extends K ? KEY : never]: T[KEY];
} : PartialDeep<T> & {
    [KEY in keyof T as KEY extends FirstKey<K> ? KEY : never]: RequiredOnlyWhere<T[KEY], S.Join<L.Omit<S.Split<K, '.'>, 0>>>;
};
export declare type EnumLike<T extends string> = T | `${T}`;
export declare type PlainObjectType<T> = OmitWhere<T, Function>;
